mapper.select:
SELECT DISTINCT n.`notictop`,n.`noticcontent`,n.`createtime`,n.`noticid`, u.`name` publishUser,n.`corid` FROM `notic` n LEFT JOIN `sys_user` u ON n.`notictuserid` = u.`user_id` LEFT JOIN noticeduser nd ON nd.`noticid` = n.`noticid` LEFT JOIN `sys_user` u1 ON nd.`noticteduserid` = u1.`user_id`
AND u.`name` LIKE CONCAT('%',#{pd.publishUser,jdbcType=VARCHAR},'%')
AND n.`corid` = #{pd.corId,jdbcType=INTEGER}
AND u1.`name` LIKE CONCAT('%',#{pd.receiveUser,jdbcType=VARCHAR},'%')
AND n.`createtime` >= #{pd.startTime} AND n.`createtime` <= #{pd.endTime}

SELECT DISTINCT n.`notictop`,n.`noticcontent`,n.`createtime`,n.`noticid`, u.`name` publishUser,n.`corid` FROM `notic` n LEFT JOIN `sys_user` u ON n.`notictuserid` = u.`user_id` LEFT JOIN noticeduser nd ON nd.`noticid` = n.`noticid` LEFT JOIN `sys_user` u1 ON nd.`noticteduserid` = u1.`user_id`
AND n.`corid` = #{pd.corId,jdbcType=INTEGER}
AND u1.`user_id` = #{pd.receiveUserId,jdbcType=INTEGER}
